CREATE TABLE SQLD_05 (
    COL1 VARCHAR2(10),
    COL2 NUMBER
);

INSERT INTO SQLD_05 VALUES('AB', NULL);
ALTER TABLE SQLD_05 MODIFY COL2 NUMBER(2);
ALTER TABLE SQLD_05 MODIFY COL2 DEFAULT 10;
INSERT INTO SQLD_05(COL1) VALUES('CED');
INSERT INTO SQLD_05 VALUES('PST', NULL);

ALTER TABLE SQLD_05 MODIFY COL1 VARCHAR2(6);
ALTER TABLE SQLD_05 MODIFY COL1 DEFAULT 'SWEET';

INSERT INTO SQLD_05(COL2) VALUES(22);

ALTER TABLE SQLD_05 MODIFY COL1 VARCHAR2(6) DEFAULT 'ONION';

INSERT INTO SQLD_05(COL2) VALUES(22);

ALTER TABLE SQLD_05 ADD COL3 VARCHAR2(10 BYTE) NULL; 

INSERT INTO SQLD_05 VALUES('HBN', '', 50);
INSERT INTO SQLD_05 VALUES('HBN', 50, '');  -- '' 빈 문자열을 삽입하면 NULL값으로 데이터가 들어간다.
INSERT INTO SQLD_05(COL3) VALUES(' ');      -- ' ' 라고 공백을 입력하면 공백이 데이터로 들어간다. ''와 ' '는 엄연히 다름!ㄴ


ALTER TABLE SQLD_05 ADD COL4 NUMBER;


SELECT COL1, COL2,
  CASE WHEN COL1 = 'HBN' THEN 'SWEET'   -- COL1의 값이 HBN인 것은 SWEET으로,
    ELSE 'PST'     -- 나머지 COL1의 값은 PST로 바꿔서
   END AS NAMED    -- NAMED라는 별칭으로 COL1,COL2 조회하기
  FROM SQLD_05;
  
SELECT COL1,
  CASE COL1 WHEN 'HBN' THEN 'KIM'
    ELSE 'PARK'
   END AS FIRST
  FROM SQLD_05;



ALTER TABLE SQLD_05 ADD CO45 DATE;
INSERT INTO SQLD_05(CO45) VALUES(SYSDATE);
INSERT INTO SQLD_05 VALUES('OTEI', 17, '', TO_DATE('2022-02-21', 'YYYY-MM-DD'));

SELECT 'Oracle World' AS result1
     , REPLACE('Oracle World', 'Oracle', 'Ora') AS result2
  FROM DUAL;



SELECT 'Oracle 
    World' AS result1
     , REPLACE('Oracle World', 'Oracle', 'Ora') AS result2
     , REPLACE('Oracle World', CHR(10)) AS result3
  FROM DUAL;



















































